/* Function  structure for function  `get_nilai_absensi` */

/*!50003 DROP FUNCTION IF EXISTS `get_nilai_absensi` */;
DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` FUNCTION `get_nilai_absensi`(NIP_ VARCHAR(20), BULAN_ VARCHAR(20)) RETURNS decimal(10,2)
    DETERMINISTIC
BEGIN
	DECLARE nilai_ DECIMAL(10,2);
	  select ((avg(a.absen_pagi)+avg(a.absen_siang)+avg(a.absen_sore))/3*100) into nilai_ 
	  from absensi_tbl a 
	  where a.nip = NIP_ and month(a.tanggal) = BULAN_
	  group by month(a.tanggal);
	return nilai_;
END */$$
DELIMITER ;

/* Function  structure for function  `get_nilai_absensi_harian` */

/*!50003 DROP FUNCTION IF EXISTS `get_nilai_absensi_harian` */;
DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` FUNCTION `get_nilai_absensi_harian`(NIP_ VARCHAR(20), TANGGAL_ DATE) RETURNS decimal(10,2)
BEGIN
  DECLARE nilai_ DECIMAL (10, 2) ;
  SELECT 
    (
      (
        AVG(a.absen_pagi) + AVG(a.absen_siang) + AVG(a.absen_sore)
      ) 
    ) INTO nilai_ 
  FROM
    absensi_tbl a 
  WHERE a.nip = NIP_ 
    AND a.tanggal = TANGGAL_ ;
  RETURN nilai_ ;
END */$$
DELIMITER ;

/* Function  structure for function  `get_nilai_kinerja` */

/*!50003 DROP FUNCTION IF EXISTS `get_nilai_kinerja` */;
DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` FUNCTION `get_nilai_kinerja`(NIP_ VARCHAR(20), SKPD_ VARCHAR(20), JABATAN_ VARCHAR(20), BULAN_ VARCHAR(20)) RETURNS decimal(10,2)
    DETERMINISTIC
BEGIN
	DECLARE nilai_ DECIMAL(10,2);
	  select avg(pp.nilai) into nilai_ from kegiatan_tbl pp
	where pp.nip = NIP_ and pp.kd_skpd=SKPD_ and pp.kd_position = JABATAN_ and month(pp.tgl_kegiatan)=BULAN_
	group by month(pp.tgl_kegiatan);
	return nilai_;
END */$$
DELIMITER ;

