/* Function  structure for function  `get_nilai_prilaku` */

/*!50003 DROP FUNCTION IF EXISTS `get_nilai_prilaku` */;
DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` FUNCTION `get_nilai_prilaku`(NIP_ VARCHAR(20), SKPD_ VARCHAR(20), JABATAN_ VARCHAR(20), BULAN_ VARCHAR(20)) RETURNS decimal(10,2)
    DETERMINISTIC
BEGIN
	DECLARE nilai_ DECIMAL(10,2);
	  SELECT ROUND(((SUM(pp.orientasi_pelayanan)/ COUNT(pp.orientasi_pelayanan) + SUM(pp.integritas)/ COUNT(pp.integritas) + SUM(pp.komitmen)/ COUNT(pp.komitmen) + SUM(pp.disiplin)/ COUNT(pp.disiplin) + SUM(pp.kerjasama)/ COUNT(pp.kerjasama) + SUM(pp.kepemimpinan)/ COUNT(pp.kepemimpinan) + SUM(pp.guidance)/ COUNT(pp.guidance))/700)*100, 2) into nilai_ FROM penilaian_pribadi_tbl pp 
	  where pp.nip = NIP_ and pp.kd_skpd=SKPD_ and pp.kd_position =JABATAN_ and month(pp.tanggal)=BULAN_
	  group by month(pp.tanggal);
	return nilai_;
END */$$
DELIMITER ;

/* Function  structure for function  `get_nilai_prilaku_harian` */

/*!50003 DROP FUNCTION IF EXISTS `get_nilai_prilaku_harian` */;
DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` FUNCTION `get_nilai_prilaku_harian`(
  NIP_ VARCHAR (20),
  SKPD_ VARCHAR (20),
  JABATAN_ VARCHAR (20),
  TANGGAL_ DATE 
) RETURNS decimal(10,2)
BEGIN
  DECLARE nilai_ DECIMAL (10, 2) ;
  SELECT 
    ROUND(
      (
        (
          SUM(pp.orientasi_pelayanan) / COUNT(pp.orientasi_pelayanan) + SUM(pp.integritas) / COUNT(pp.integritas) + SUM(pp.komitmen) / COUNT(pp.komitmen) + SUM(pp.disiplin) / COUNT(pp.disiplin) + SUM(pp.kerjasama) / COUNT(pp.kerjasama) + SUM(pp.kepemimpinan) / COUNT(pp.kepemimpinan) + SUM(pp.guidance) / COUNT(pp.guidance)
        ) / 700
      ) * 100,
      2
    ) INTO nilai_ 
  FROM
    penilaian_pribadi_tbl pp 
  WHERE pp.nip = NIP_ 
    AND pp.kd_skpd = SKPD_ 
    AND pp.kd_position = JABATAN_ 
    AND pp.tanggal = TANGGAL_ ;
  RETURN nilai_ ;
END */$$
DELIMITER ;

/* Function  structure for function  `get_sum_by_cols` */

/*!50003 DROP FUNCTION IF EXISTS `get_sum_by_cols` */;
DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` FUNCTION `get_sum_by_cols`(COLS_ VARCHAR(20), VALUE_ VARCHAR(20)) RETURNS decimal(10,2)
    DETERMINISTIC
BEGIN
	DECLARE nilai_ DECIMAL(10,2);
